Actionscript.script:
onClipEvent (load) {
	count = 0;
	landedon = false;
}
onClipEvent (enterFrame) {
	if (_root.PauseGame == false && _root.playpause == false) {
		count++;
		_y += 0.5;
		_x += Math.cos(count)*.5;
		//
		dx = _x-_root.Course.Char._x+_parent._x;
		dy = _y-_root.Course.Char._y+_parent._y;
		distance = Math.sqrt(dx*dx+dy*dy);
		//      
		newx = _x+Math.sin(_root.angleToRadians(_rotation))*distance;
		newy = _y+Math.cos(_root.angleToRadians(_rotation))*distance;
		diffy = newy-oldy;
		diffx = newx-oldx;
		oldx = newx;
		oldy = newy;
		//
		Y = Math.cos(_root.angleToRadians(_rotation))*distance;
		X = Math.sin(_root.angleToRadians(_rotation))*distance;
		//
		if (hitTest((_root.Course.Char._x*_root.coursescale/100)+_root.Course._x, ((_root.Course.Char._y+3)*_root.coursescale/100)+_root.Course._y, true)) {
			_root.Course.Char._x += this.diffx;
			_root.Course.Char._y += this.diffY;
			//_root.Course.Char.xspeed = _root.groundFriction(_root.Course.Char.xspeed, 0.3, 1.1);
			if (landedon == false) {
				_root.Course.Char.xspeed -= this.diffx;
				//
			}
			landedon = true;
		} else {
			if (landedon == true) {
				_root.Course.Char.xspeed += this.diffx;
				//_root.Course.Char.yspeed += this.diffy/2;
			}
			landedon = false;
		}
	}
	//  
}

onClipEvent (load) {
	count = 0;
	landedon = false;
	speed = -0.4;
	fallspeed = 0;
}
onClipEvent (enterFrame) {
	if (_root.PauseGame == false && _root.playpause == false) {
		_rotation += speed;
		_y += fallspeed;
		//
		ydistance = (_y+_parent._y)-_root.Course.Char._y;
		xdistance = (_x+_parent._x)-_root.Course.Char._x;
		distance = Math.sqrt(Math.pow(xdistance, 2)+Math.pow(ydistance, 2));
		rot = Math.atan2(ydistance, xdistance);
		xdiff = Math.cos(rot)*distance-Math.cos(rot+_root.angleToRadians(this.speed))*distance;
		ydiff = Math.sin(rot)*distance-Math.sin(rot+_root.angleToRadians(this.speed))*distance;
		//
		if (hitTest((_root.Course.Char._x*_root.coursescale/100)+_root.Course._x, ((_root.Course.Char._y+3)*_root.coursescale/100)+_root.Course._y, true) && _root.Course.Char.air == false) {
			_root.Course.Char._x += xdiff;
			_root.Course.Char._y += ydiff+fallspeed;
			//_root.Course.Char.xspeed = _root.groundFriction(_root.Course.Char.xspeed, 0.3, 1.1);
			if (landedon == false) {
				_root.Course.Char.xspeed -= xdiff;
				//
			}
			landedon = true;
		} else {
			if (landedon == true) {
				_root.Course.Char.xspeed += xdiff;
				_root.Course.Char.yspeed += ydiff;
			}
			landedon = false;
		}
	}
}

