onClipEvent (load) {
	invis = true;
	starnum = 23;
	timecount = 0;
	//
	if (_root.Course.coincountnum == undefined) {
		_root.Course.coincountnum = 0;
	} else {
		_root.Course.coincountnum++;
	}
	if (_root["coindata"+_root.playingcourse][0] == undefined) {
		_root["coindata"+_root.playingcourse] = [""];
	}
	coinnum = _root.Course.coincountnum;
	if (_root["coindata"+_root.playingcourse][coinnum] == 1) {
		invis = false;
	}
}
onClipEvent (enterFrame) {
	timecount++;
	if (_root.SilverStars>=5 && timecount>12) {
		_root["coindata"+_root.playingcourse][coinnum] = 1;
		invis = false;
	}
}

onClipEvent (load) {
	saveX = _x-1350;
	saveY = _y-30;
	saverot = Math.atan2(saveY, saveX);
	distance = Math.sqrt((saveX*saveX)+(saveY*saveY));
}
onClipEvent (enterFrame) {
	angle = _root.angleToRadians(_root.Course.Platforms.Wheel._rotation+90);
	rotation = angle+saverot;
	_rotation = _root.radiansToAngle(rotation);
	newx = Math.sin(rotation)*distance;
	newy = -Math.cos(rotation)*distance;
	_x = newx+1350;
	_y = newy+30;
}

