Actionscript.script:
onClipEvent (load) {
	_visible = false;
}
onClipEvent (enterFrame) {
	if (this.hitTest(_root.Course.Char.charClip.body)) {
		if (_root.PauseGame == false && _root.playpause == false) {
			//_root.Course.Char.yspeed = _root.groundFriction(_root.Course.Char.yspeed, 0.015, 1.001);
			if (savezoom == undefined) {
				savezoom = _root.CameraZoom;
			}
			if (_root.CameraZoom>85) {
				_root.CameraZoom -= 1;
			}
		}
	} else {
		if (savezoom !== undefined) {
			if (_root.CameraZoom<savezoom) {
				_root.CameraZoom += 1;
			} else {
				savezoom = undefined;
			}
		}
	}
}

onClipEvent (load) {
	_visible = false;
}
onClipEvent (enterFrame) {
	if (this.hitTest(_root.Course.Char.charClip.body)) {
		if (_root.PauseGame == false && _root.playpause == false) {
			//_root.Course.Char.yspeed = _root.groundFriction(_root.Course.Char.yspeed, 0.015, 1.001);
			if (savezoom == undefined) {
				savezoom = _root.CameraZoom;
			}
			if (_root.CameraZoom>65) {
				_root.CameraZoom -= 1;
			}
		}
	} else {
		if (savezoom !== undefined) {
			if (_root.CameraZoom<savezoom) {
				_root.CameraZoom += 1;
			} else {
				savezoom = undefined;
			}
		}
	}
}

onClipEvent (load) {
	count = 0;
	rotspeed = 0.03;
	landedon = false;
}
onClipEvent (enterFrame) {
	_rotation += rotspeed;
	count += 1;
	if (count>260) {
		rotspeed *= -1;
		count = 0;
	}
	//   
	dx = _x-_root.Course.Char._x+_parent._x;
	dy = _y-_root.Course.Char._y+_parent._y;
	distance = Math.sqrt(dx*dx+dy*dy);
	//      
	newx = Math.sin(_root.angleToRadians(_rotation))*distance;
	newy = Math.cos(_root.angleToRadians(_rotation))*distance;
	diffy = newy-oldy;
	diffx = newx-oldx;
	oldx = newx;
	oldy = newy;
	//
	Y = Math.cos(_root.angleToRadians(_rotation))*distance;
	X = Math.sin(_root.angleToRadians(_rotation))*distance;
	//
	if (hitTest((_root.Course.Char._x*_root.coursescale/100)+_root.Course._x, ((_root.Course.Char._y+3)*_root.coursescale/100)+_root.Course._y, true)) {
		_root.Course.Char._x += this.diffx;
		_root.Course.Char._y += this.diffY;
		//_root.Course.Char.xspeed = _root.groundFriction(_root.Course.Char.xspeed, 0.3, 1.1);
		if (landedon == false) {
			_root.Course.Char.xspeed -= this.diffx;
			//
		}
		landedon = true;
	} else {
		if (landedon == true) {
			_root.Course.Char.xspeed += this.diffx;
			//_root.Course.Char.yspeed += this.diffy/2;
		}
		landedon = false;
	}
	// 
}

