	//example   = "You can jump in the cannon\nand blast off!";
	signmessage = "The Gaps between this platform and the next is pretty large;  You'd have to be quite creative as to how you get across.  Might I recommend Diving;  It is dangerous, reckless, and increases your distance and overall pace by a ton;  or perhaps slowly gliding across with Spin Attack?  There's even using the enemies themselves to hop across, and also Triple Jumping!";
}

onClipEvent (load) {
	DirectionX = "Left";
	DirectionY = "Up";
	distanceX = 100
	distanceY = 100
	accelX = 0.1
	accelY = 0.5
	speedX = 0.4
	speedY = 2
	scale = 1
	//
	disapearcount = 75
}

onClipEvent (load) {
	side = "Both";
	Xpower = 40;
	Ypower = 50;
	keycount = 0;
	if (_root.lvl8keyA == true) {
		keycount++;
	}
	if (_root.lvl8keyB == true) {
		keycount++;
	}
	if (_root.lvl8keyC == true) {
		keycount++;
	}
	if (_root.lvl8keyD == true) {
		keycount++;
	}
	//  
	if (_root.Course.coincountnum == undefined) {
		_root.Course.coincountnum = 0;
	} else {
		_root.Course.coincountnum++;
	}
	if (_root["coindata"+_root.playingcourse][0] == undefined) {
		_root["coindata"+_root.playingcourse] = [""];
	}
	coinnum = _root.Course.coincountnum;
	if (_root["coindata"+_root.playingcourse][coinnum] == 1) {
		cannonsetup = true;
	} else {
		cannonsetup = false;
	}
	count = 0;
	// 
}
onClipEvent (enterFrame) {
	count++;
	if (keycount>=2 && count>12) {
		_root["coindata"+_root.playingcourse][coinnum] = 1;
		cannonsetup = true;
	}
}

