onClipEvent (enterFrame) {
	if (this.hitTest(_root.Course.Char.charClip.body)) {
		_root.Course.Char._y -= 400;
		//_root.MarioHit(0, 0, 0, -1, 32, undefined, true);
		_root.MarioHit(0, 6, -7, -2, 10, "Mario-Squish", true);
		// a  is the HP lost
		// b is the blink time
		// c is the velocity Y amount mario is knocked back
		// d is the velocity X mario is moved back
		// e is the amount of time Mario gets stunned
		// f is the noise Mario or Luigi makes
		// g is if mario gets knocked to the ground
	}
}

