onClipEvent (enterFrame) {
	if (this.hitTest(_root.Course.Char.charClip.body)) {
		if (_root.PauseGame == false && _root.playpause == false) {
			//_root.Course.Char.yspeed = _root.groundFriction(_root.Course.Char.yspeed, 0.015, 1.001);
			if (savezoom == undefined) {
				savezoom = _root.CameraZoom;
			}
			if (_root.CameraZoom>75) {
				_root.CameraZoom -= 1;
			}
		}
	} else {
		if (savezoom !== undefined) {
			if (_root.CameraZoom<savezoom) {
				_root.CameraZoom += 1;
			} else {
				savezoom = undefined;
			}
		}
	}
}

onClipEvent (load) {
	_alpha = 0;
	DirectionX = "Left";
	DirectionY = "Up";
	distanceX = 0;
	distanceY = 300;
	accelX = 0;
	accelY = 0.2;
	speedX = 0;
	speedY = 3
	scale = 3;
	//
	disapearcount = 100+10
	count = 0;
	OEF = function () {
		count++;
		_root.Camcenter = this;
		if (count>5) {
			count = 0;
			//
			_root.PlayMessage("A platform has appeareed.");
			OEF = function () {
				//
				_root.playpause = true;
				_root.Camcenter = this;
				//
				count++;
				if ((_root.KeySPACE() || _root.KeyZ() || _root.KeyX()) && count>5) {
					_root.StopMessage();
					_root.Camcenter = "None";
					//
					count = 0;
					OEF = function () {
						count++;
						if (count>5) {
							_root.playpause = false;
							OEF = null;
						}
					};
				}
			};
		}
	};
	//
}
onClipEvent (enterFrame) {
	if (_alpha<100) {
		this._alpha += 1;
	} else {
		_alpha = 100;
	}
	OEF();
	//
}

