Actionscript.script:
stop();
newx = _x;
newy = _y;
oldx = _x;
oldy = _y;
//
newerx = _x;
newery = _y;
olderx = _x;
oldery = _y;
if (_parent.exists == undefined && isNaN(_root.Course.BPlatforms["CustomMovingPL"+_parent.capetdepth].exists)) {
	_parent.capetdepth = _root.Course.BPlatforms.getNextHighestDepth();
	_root.Course.BPlatforms.attachMovie("8-10CarpetPlatformBClip", "CustomMovingPL"+_parent.capetdepth, _parent.capetdepth, {_x:_x+_parent._x, _y:_y+_parent._y, scale:scale, exists:true});
	_parent.exists = true;
}
onEnterFrame = function () {
	if (_root.PauseGame == false) {
		//
		if (isNaN(accelx)) {
			accelx = 0;
		}
		if (isNaN(accely)) {
			accely = 0;
		}
		if (isNaN(diffx)) {
			diffx = 0;
		}
		if (isNaN(diffy)) {
			diffy = 0;
		}
		tellTarget (_root.Course.BPlatforms["CustomMovingPL"+_parent.capetdepth]) {
			if (hitTest(((_root.Course.Char._x+this.diffx+this.accelx)*_root.coursescale/100)+_root.Course._x, ((_root.Course.Char._y+this.diffy+this.accely+3)*_root.coursescale/100)+_root.Course._y, true)) {
				_root.Course.Char._x += this.diffx+this.accelx;
				_root.Course.Char._y += this.diffy;
				_root.Course.Char.xspeed = _root.groundFriction(_root.Course.Char.xspeed, 0.3, 1.1);
				//
				if (hitTest(((_root.Course.Char._x+this.diffx+this.accelx)*_root.coursescale/100)+_root.Course._x, ((_root.Course.Char._y+this.diffy+this.accely-3)*_root.coursescale/100)+_root.Course._y, true)) {
					_root.Course.Char._y--;
				}
				//                
				if (landedon == false) {
					_root.Course.Char.xspeed -= this.diffx;
				}
				landedon = true;
				_parent.landedon = true;
			} else {
				if (landedon == true) {
					_root.Course.Char.xspeed += this.diffx;
					_root.Course.Char.yspeed += this.diffy/2;
				}
				landedon = false;
				_parent.landedon = false;
			}
			_y = this._y+this._parent._y;
			_x = this._x+this._parent._x;
		}
		//
		newx = _x;
		newy = _y;
		diffy = newy-oldy;
		diffx = newx-oldx;
		oldx = newx;
		oldy = newy;
		//
		newerx = diffx;
		newery = diffy;
		accely = newery-oldery;
		accelx = newerx-olderx;
		olderx = newerx;
		oldery = newery;
	}
};
