Actionscript.script:
_root.AreaTextClipF("Outskirts of Bowser's Castle:"+newline+"<i>Kamek's Castle</i>", 30);
_root.Starnamenum = 51
defeatedcount = 0;
onscreencount = 0;
stop();
_root.Restartcoins();

onClipEvent (load) {
	invis = true;
	starnum = 51;
	timecount = 0;
	alternateoldframe = true;
}
onClipEvent (enterFrame) {
	timecount++;
	if (_parent.bully1.deadcount>32 && _parent.bully1.deadcount !== undefined && timecount>12) {
		_root.Course.FrontGFX.gotoAndStop(1);
		invis = false;
	}
}

onClipEvent (load) {
	restart = true;
	scale = 2;
	Direction = -1;
	//Message = "Heh, you fell into my trap. If you want to get this Shine Sprite you will have to throw me off of this platform.";
	bounceto = 815;
}
onClipEvent (enterFrame) {
	if (HP == 3) {
		scale = 2;
	}
	if (HP == 2) {
		scale = 2.5;
	}
	if (HP == 1) {
		scale = 3;
	}
	if (HP == 0) {
		scale -= 0.025;
		scale = Math.max(scale, 0.5);
	}
	_yscale = scale*50;
	if (_xscale>0) {
		_xscale = scale*50;
	} else {
		_xscale = -scale*50;
	}
	_x = Math.max(_x, 520);
	_x = Math.min(_x, 1100);
}

onClipEvent (load) {
	count = 0;
	rotspeed = 0.03;
	landedon = false;
}
onClipEvent (enterFrame) {
	_rotation += rotspeed;
	count += 1;
	if (count>260) {
		rotspeed *= -1;
		count = 0;
	}
	//   
	dx = _x-_root.Course.Char._x+_parent._x;
	dy = _y-_root.Course.Char._y+_parent._y;
	distance = Math.sqrt(dx*dx+dy*dy);
	//      
	newx = Math.sin(_root.angleToRadians(_rotation))*distance;
	newy = Math.cos(_root.angleToRadians(_rotation))*distance;
	diffy = newy-oldy;
	diffx = newx-oldx;
	oldx = newx;
	oldy = newy;
	//
	Y = Math.cos(_root.angleToRadians(_rotation))*distance;
	X = Math.sin(_root.angleToRadians(_rotation))*distance;
	//
	if (hitTest((_root.Course.Char._x*_root.coursescale/100)+_root.Course._x, ((_root.Course.Char._y+3)*_root.coursescale/100)+_root.Course._y, true)) {
		_root.Course.Char._x += this.diffx;
		_root.Course.Char._y += this.diffY;
		//_root.Course.Char.xspeed = _root.groundFriction(_root.Course.Char.xspeed, 0.3, 1.1);
		if (landedon == false) {
			_root.Course.Char.xspeed -= this.diffx;
			//
		}
		landedon = true;
	} else {
		if (landedon == true) {
			_root.Course.Char.xspeed += this.diffx;
			//_root.Course.Char.yspeed += this.diffy/2;
		}
		landedon = false;
	}
	// 
}

