Actionscript.script:
stop();
onEnterFrame = function () {
	if (this.warp1.hitTest(_root.Course.Char.charClip.feet)) {
		_root.Course.Char.attack = true;
		_root.Course.Char.xspeed = 0;
		_root.Course.Char.yspeed = 0;
		_root.Course.Char.attackFrame = "none";
		warp = true;
		warpnum = 1;
	}
	if (this.warp2.hitTest(_root.Course.Char.charClip.feet)) {
		_root.Course.Char.attack = true;
		_root.Course.Char.xspeed = 0;
		_root.Course.Char.yspeed = 0;
		_root.Course.Char.attackFrame = "none";
		warp = true;
		warpnum = 2;
	}
	if (warp == true && _root.warpframe == false) {
		if (warpnum == 1) {
			_root.attachMovie("downtransition", "downtransition"+_root.getNextHighestDepth(), _root.getNextHighestDepth(), {_x:_root.screensizeX/2, _y:_root.screensizeY/2});
		}
		if (warpnum == 2) {
			_root.attachMovie("uptransition", "uptransition"+_root.getNextHighestDepth(), _root.getNextHighestDepth(), {_x:_root.screensizeX/2, _y:_root.screensizeY/2});
		}
	}
	if (_root.warpframe == true) {
		if (warpnum == 1) {
			_root.StopBGsong();
			//_root.Playinglevel = ""
			_root.changecourse("downtransition2", "9-06", 0, 0, 0, 0, true);
		}
		if (warpnum == 2) {
			//_root.Playinglevel = ""
			_root.changecourse("uptransition2", "C-3", 100, 0, -250, 0);
		}
	}
};

onClipEvent (load) {
	doorplace = "C-7-3";
	//d11
	dooropenarray = 10;
	locked = true;
	keytype = 1;
	if (_root.TotalStars>=20 || _root.CheatCastleUnlock == true) {
		removelock = true;
	}
	keytype = 10;
	//                                
	doorlockfunction = function () {
		_root.Course.Char.attack = true;
		_root.Course.Char.attackFrame = "Sign";
		_root.Course.Char.xspeed = 0;
		//
		_root.PlayMessage("You need a total of 20 Shine Sprites to unlock this door!");
		_root.Mariosound = new Sound(this);
		_root.Mariosound.attachSound("BowserLaugh");
		_root.Mariosound.start(0, 1);
		_root.Mariosound.setVolume(_root.MarioVolume);
		count = 0;
		onEnterFrame = function () {
			X = _x+_parent._x;
			_root.Course.Char._x += (X-_root.Course.Char._x)/5;
			//
			_root.playpause = true;
			//
			count++;
			if (_root.KeyZ() && count>5) {
				_root.StopMessage();
				//
				count = 0;
				onEnterFrame = function () {
					count++;
					if (count>5) {
						_root.playpause = false;
						_root.Course.Char.attack = false;
						onEnterFrame = null;
						gotoAndStop(1);
					}
				};
			}
		};
	};
}
onClipEvent (enterFrame) {
	if (_root.TotalStars>=20 || _root.CheatCastleUnlock == true) {
		removelock = true;
	}
}

onClipEvent (load) {
	starfunction = function () {
		_root.StartNewMusicAndIntroNoVar("MetalCap - Intro", "MetalCap - Repeat");
		_root.PowerTimer = 32*30;
		_root.Metal = true;
	};
}

onClipEvent (load) {
	/*if (_root.lvl4GreenSwitch == true) {
	activated = true;
	}*/
	gotoAndStop("end");
	onEnterFrame = function () {
		if (_root.C8Switch == true) {
			play();
		}
	};
	Switchfunction = function () {
		/*
		_root.Mariosound = new Sound(this);
		_root.Mariosound.attachSound("waterfall");
		_root.Mariosound.start(0, 1);
		_root.Mariosound.setVolume(_root.MarioVolume);*/
		count = 0;
		onEnterFrame = function () {
			count++;
			if (count>5) {
				count = 0;
				//
				_root.PlayMessage("Oh No! The Castle's Basement Drain has been opened! All the water will now be drained out!");
				onEnterFrame = function () {
					//
					_root.playpause = true;
					//
					count++;
					if ((_root.KeySPACE() || _root.KeyZ() || _root.KeyX()) && count>5) {
						_root.StopMessage();
						//
						count = 0;
						onEnterFrame = function () {
							count++;
							if (count>5) {
								_root.C8Switch = false;
								_root.playpause = false;
								_root.Course.Char.attack = false;
								onEnterFrame = function () {
									if (_root.C8Switch == true) {
										play();
									}
								};
								gotoAndStop("end");
							}
						};
					}
				};
			}
		};
	};
}

