			return (BP());
		};
		M13F = function () {
			_root.Course.Char.charClip.frame.gotoAndPlay(1);
			this._xscale *= -1;
			Mood = "Mad";
			StartMood = "Mad";
			M1Moo = "Mad";
			M1Txt = "......Go Away!!";
			M1Sta = function () {
				return (BP());
			};
			M1End = function () {
				return (BP());
			};
			//
			M2Moo = undefined;
			M2Txt = undefined;
			M2Sta = null;
			M2End = null;
		};
	} else {
		this._y = 500;
	}
	//
}

