Actionscript.script:
_root.StopBGsong();
_root.Fluddpow = "";
_root.Restartcoins();
//
_root.SaveFluddH = false;
_root.SaveFluddR = false;
_root.SaveFluddT = false;
_root.Fluddpow = ""
_root.Playinglevel = 0;
_root.Starnamenum = 0
//
_root.AreaTextClipF("Castle Grounds:"+newline+"<i>Outside the Castle</i>", 35);
//
color = Math.round(_root.TotalStars*1.5)+25;
color2 = Math.abs(100-color);
myColor = new Color(this);
colorTrans = new Object();
colorTrans = {ra:(color*0.5)+50-(color2/3), rb:0, ga:(color*0.5)+50-(color2/3), gb:0, ba:(color*0.5)+50-(color2/3), bb:0, aa:100, ab:0};
myColor.setTransform(colorTrans);
onEnterFrame = function () {
	color += Math.round(((_root.TotalStars*1.5)+25)-color)/7;
	if (_root.CheatCastleRestore == true) {
		color = 100;
	}
	color = Math.max(color, 0);
	color = Math.min(color, 100);
	color2 = Math.abs(100-color);
	// color = percent of stars obained
	// color 2  = percent of stars not yet obtained
	colorTrans = {ra:(color*0.75)+25-(color2/5), rb:0, ga:(color*0.9)+10-(color2/3), gb:0, ba:(color*0.9)+10-(color2/3), bb:0, aa:100, ab:0};
	myColor.setTransform(colorTrans);
};

stop();
_root.warpframe = false;
bnum = random(30)+30;
bcount = 0;
btype = random(6)+1;
//
this.createEmptyMovieClip("birdsound", _root.getNextHighestDepth());
onEnterFrame = function () {
	//
	//
	//trace("Cam : "+_root["Cam"+t+"change"])
	//
	color += Math.round(((_root.TotalStars*1.5)+25)-color)/7;
	if (_root.CheatCastleRestore == true) {
		color = 100;
	}
	//color = Math.max(color, 0);             
	//color = Math.min(color, 100);
	if (color>100) {
		oldcolor = color;
		color += Math.abs(100-color)*2.5;
	}
	color2 = Math.abs(100-color);
	// color = percent of stars obained
	// color 2  = percent of stars not yet obtained
	colorTrans = {ra:(color*0.5)+50-(color2/3), rb:0, ga:(color*0.5)+50-(color2/3), gb:0, ba:(color*0.5)+50-(color2/3), bb:0, aa:100, ab:0};
	myColor.setTransform(colorTrans);
	if (color>100) {
		color = oldcolor;
	}
	//      
	//Bird Chirp Code
	if (_root.TotalStars>20) {
		birdvolume = 1;
		//        
		bcount++;
		if (bcount>bnum) {
			birdsound = new Sound(this);
			birdsound.attachSound("Birdy"+btype);
			birdsound.start(0, 1);
			birdsound.setVolume(Math.round(_root.MarioVolume/2)*birdvolume);
			bcount = 0;
			btype = random(6)+1;
			bnum = random(50)+(73-_root.TotalStars);
		}
	}
	//                
	if (this.warp1.hitTest(_root.Course.Char.charClip.feet) && _root.Star[39] == true) {
		_root.Course.Char.attack = true;
		_root.Course.Char.xspeed = 0;
		_root.Course.Char.yspeed = 0;
		_root.Course.Char.attackFrame = "none";
		warp = true;
		warpnum = 1;
	}
	// && _root.Course.Char.yspeed<=0 && _root.TotalStars>=63
	if (this.warp2.hitTest(_root.Course.Char.charClip.feet)) {
		_root.Course.Char.attack = true;
		_root.Course.Char.xspeed = 0;
		_root.Course.Char.yspeed = 0;
		_root.Course.Char.attackFrame = "none";
		warp = true;
		warpnum = 2;
	}
	if (warp == true && _root.warpframe == false) {
		if (warpnum == 1) {
			_root.attachMovie("lefttransition", "lefttransition"+_root.getNextHighestDepth(), _root.getNextHighestDepth(), {_x:_root.screensizeX/2, _y:_root.screensizeY/2});
		}
		if (warpnum == 2) {
			_root.attachMovie("righttransition", "righttransition"+_root.getNextHighestDepth(), _root.getNextHighestDepth(), {_x:_root.screensizeX/2, _y:_root.screensizeY/2});
		}
		warp = false;
	}
	if (_root.warpframe == true) {
		if (warpnum == 1) {
			_root.changecourse("lefttransition2", "8-E1-2-2", 964, -783, 964, -783, true);
		}
		if (warpnum == 2) {
			_root.Fluddpow = ""
			//_root.StopBGsong();
			_root.Restartcoins();
			_root.changecourse("righttransition2", "K-1", 0, 0, 0, 0, true);
		}
		//_root.warpframe = false;                                            
	}
};

onClipEvent (load) {
	_visible = false;
	mess = "(Hint: Press <b>Up</b> to enter a door)";
}
onClipEvent (enterFrame) {
	if (_root.TotalStars<=0 && _root.Star[39] == false) {
		if (this.hitTest(_root.Course.Char.charClip.body)) {
			if (_root.PauseGame == false && _root.playpause == false) {
				_root.TextHint = mess;
			}
		} else {
			if (mess == _root.TextHint) {
				_root.TextHint = "";
			}
		}
	}
}

onClipEvent (load) {
	_visible = false;
}
onClipEvent (enterFrame) {
	if (this.hitTest(_root.Course.Char.charClip.body)) {
		if (_root.PauseGame == false && _root.playpause == false) {
			//_root.Course.Char.yspeed = _root.groundFriction(_root.Course.Char.yspeed, 0.015, 1.001);
			if (savezoom == undefined) {
				savezoom = _root.CameraZoom;
			}
			if (_root.CameraZoom>65) {
				_root.CameraZoom -= 1;
			}
		}
	} else {
		if (savezoom !== undefined) {
			if (_root.CameraZoom<savezoom) {
				_root.CameraZoom += 1;
			} else {
				savezoom = undefined;
			}
		}
	}
}

onClipEvent (load) {
	_visible = false;
}
onClipEvent (enterFrame) {
	if (this.hitTest(_root.Course.Char.charClip.body)) {
		if (_root.PauseGame == false && _root.playpause == false) {
			//_root.Course.Char.yspeed = _root.groundFriction(_root.Course.Char.yspeed, 0.015, 1.001);
			if (savezoom == undefined) {
				savezoom = _root.CameraZoom;
			}
			if (_root.CameraZoom>65) {
				_root.CameraZoom -= 1;
			}
		}
	} else {
		if (savezoom !== undefined) {
			if (_root.CameraZoom<savezoom) {
				_root.CameraZoom += 1;
			} else {
				savezoom = undefined;
			}
		}
	}
}

onClipEvent (load) {
	_visible = false;
}
onClipEvent (enterFrame) {
	if (_root.PauseGame == false && _root.playpause == false) {
		if (this.hitTest(_root.Course.Char.charClip.body)) {
			_root.changestep = 3;
			_root.StepSound = "step-wetwood";
		}
	}
}

onClipEvent (load) {
	starfunction = function () {
		_root.StartNewMusicAndIntroNoVar("WingCap - Intro", "WingCap - Repeat");
		_root.PowerTimer = 32*30;
		_root.WingCap = true;
	};
}

onClipEvent (load) {
	if (_root.TotalStars<63) {
		cannonsetup = false;
	}
	side = "Both";
	Xpower = 30;
	Ypower = 40;
	count = 0;
}
onClipEvent (enterFrame) {
	count++;
	if (_root.TotalStars>=63 && _root.Course.Char.attack == false && count>12) {
		cannonsetup = true;
	}
}

onClipEvent (load) {
	//example   = "You can jump in the cannon\nand blast off!";
	signmessage = "Left Leads to Tutorial\nRight Leads to Castle";
}

onClipEvent (load) {
	doorplace = "C-8-3";
		doortype = 4;
	//d10
	locked = true;
	keytype = 4;
	// Changed to locked till Kamek Battle
	unlockitem = "GreenStar";
	if ((_root.Star[50] == true && _root.BowserKey1 == true) || _root.CheatCastleUnlock == true) {
		removelock = true;
	}
	dooropenarray = 9;
	//                              
	doorlockfunction = function () {
		_root.Course.Char.attack = true;
		_root.Course.Char.attackFrame = "Sign";
		_root.Course.Char.xspeed = 0;
		//
		_root.PlayMessage("This door is locked!");
		_root.Mariosound = new Sound(this);
		_root.Mariosound.attachSound("Bowser - Laugh Muffled");
		_root.Mariosound.start(0, 1);
		_root.Mariosound.setVolume(_root.MarioVolume);
		count = 0;
		onEnterFrame = function () {
			X = _x+_parent._x;
			_root.Course.Char._x += (X-_root.Course.Char._x)/5;
			//
			_root.playpause = true;
			//
			count++;
			if (_root.KeyZ() && count>5) {
				_root.StopMessage();
				//
				count = 0;
				onEnterFrame = function () {
					count++;
					if (count>5) {
						_root.playpause = false;
						_root.Course.Char.attack = false;
						onEnterFrame = null;
						gotoAndStop(1);
					}
				};
			}
		};
	};
}
